","keccak256":"0x2526b7c6a366fc211bbc176078ab02b83833e7682abcc5b3bed9d700b34e2a3e","license":"MIT"},"contracts/Criptonix.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
pragma abicoder v2;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "hardhat/console.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";


contract Criptonix is Initializable,ERC1155BurnableUpgradeable, AccessControlUpgradeable, ReentrancyGuardUpgradeable {
  using SafeMath for uint256;
  string private _baseUri;
  string private _contractUri;
  uint256 public idCollection = 1;
  uint256 private nftId = 1;
  address public USDTAddress;
  address private recipient;
  address[] public whitelistArr;
  struct tokenData {
    address creator;
    uint256 idCollection;
    uint256 born;
    bool active;
  }
  struct Collection {
    bool isPublic;
    uint256 desactiveCost;
    uint256 madurationStartedAt;
    uint256 remainAmountToSell;
    uint256 TH;
    uint256 price;
    uint256 madurityTime;
    uint256 tokenLife;
    uint256 amount;
    uint256 amountToStartMaduration;
    uint256 energyCost;
    string description;
    string ipfsHash;
  }
  struct pagoNFTinactivo {
    uint amountOfDays;
    uint amountOfUSDT;
    uint date;
  }
  struct User {
    uint256[] idsForUser;
    uint[] rewardsAlreadyPaid;
    mapping(uint=> uint)  daysPerReward;
    mapping(uint=>pagoNFTinactivo[]) inactivoPays;
  }

  enum Status { INACTIVE, MADURATION }

  // save all NFT IPFS hashes
  mapping(uint256 => string) public ipfsHashes;
  mapping(uint256 => tokenData) public tokenDatas;
  mapping(uint256 => Collection) public collections;
  mapping(address => bool) public whitelisted;
  mapping(uint256 => uint[]) public nftsOfCollection;
  mapping(address=> User) internal userInfo;

  //EVENTS
  event rewardPaid(uint256 rewardId, address user);
  event desactivePaid(uint256 nft_id, address user, uint amountOfDays);
  event NewNFT(address creator, uint256 amount, uint256 id);
  event BurnNFT(address creator, uint256 amount, uint256 id);
  event CollectionCreated(
    uint256 price,
    uint256 madurityTime,
    uint256 tokenLife,
    uint256 amount,
    uint256 energyCost,
    string description,
    string ipfsHash
  );
  event Purchased(uint256 amount, uint256 born);

  //MODIFIERS
  modifier onlyOwner() {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Only owner can access");
    _;
  }
  modifier isPublicCollection(uint256 _collectionID) {
    if (!collections[_collectionID].isPublic) {
      require(whitelisted[msg.sender], "This collection is private");
    }
    _;
  }

  function initialize(
    string memory contractUri,
    address _USDTAddress,
    address _recipient
  )  public initializer {
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _baseUri = "ipfs://";
    _contractUri = string(abi.encodePacked("ipfs://", contractUri));
    USDTAddress = _USDTAddress;
    recipient = _recipient;
  }

  /// VIEW FUNCTIONS
  function getNFTByColleccion(uint _id) public view returns (uint[] memory) {
    return nftsOfCollection[_id];
  }
  function rewardsPaid(address _user) public view returns (uint[] memory) {
    return userInfo[_user].rewardsAlreadyPaid;
  }
  function getDaysPerReward(address _user,uint _rewardID) public view returns (uint) {
    return userInfo[_user].daysPerReward[_rewardID];
  } 
  function contractURI() public view returns (string memory) {
    return _contractUri;
  }
  function getDaysInactivosPaid(address _user, uint nft_id) public view returns (pagoNFTinactivo[] memory) {
    return userInfo[_user].inactivoPays[nft_id];
  }

  function getUserNftsId(address _account)
    public
    view
    returns (uint256[] memory)
  {
    return userInfo[_account].idsForUser;
  }

  function getTokenData(uint256 tokenId)
    public
    view
    returns (tokenData memory)
  {
    return tokenDatas[tokenId];
  }

  function getIpfsHash(uint256 collectionID)
    public
    view
    returns (string memory)
  {
    return collections[collectionID].ipfsHash;
  }

  /// PUBLIC FUNCTIONS
  function howManyNFTs(uint collectionID,address _userAddress) public view returns(uint) {
      uint count=0;
      uint[] memory _nftIDs=getUserNftsId(_userAddress);
      for(uint i=0;i<_nftIDs.length;i++) {
            Collection memory collectionToUse =
      collections[collectionID];
      if(tokenDatas[_nftIDs[i]].idCollection==collectionID &&  block.timestamp <
        (tokenDatas[_nftIDs[i]].born +
          collectionToUse.madurityTime +
          collectionToUse.tokenLife) && balanceOf(_userAddress,_nftIDs[i])>0 && tokenDatas[_nftIDs[i]].active ) {
            count++;
          }
      }
      return count;
  }
  function payEnergyCost(uint rewardID,uint collectionID,uint amountOfDays) public {
    Collection memory collectionToUse =
      collections[collectionID];
      require(amountOfDays>=10*18,"At least one day");
      require(collectionToUse.madurationStartedAt > 0,"Maduration not started yet" );
      require(block.timestamp >
        (collectionToUse.madurationStartedAt + collectionToUse.madurityTime),"Maduration not finish");
    uint count= howManyNFTs(collectionID,msg.sender);
    require(count>0,"No NFTS in this collection");
    require(
      ERC20(USDTAddress).transferFrom(
        msg.sender,
        recipient,
        amountOfDays.mul(collectionToUse.energyCost).div(10**30).mul(count)
      ),
      "The usdt transfer failed"
    );

    userInfo[msg.sender].rewardsAlreadyPaid.push(rewardID);
    userInfo[msg.sender].daysPerReward[rewardID]=amountOfDays;
    emit rewardPaid(rewardID, msg.sender);
  }
  function payInactiveCost(uint amountOfDays,uint nft_id) public {

      require(amountOfDays>=10*18,"At least one day");
      require(!tokenDatas[nft_id].active, "This NFT is active");
      require(tokenDatas[nft_id].creator==msg.sender,"This is not your NFT");
    Collection memory collectionToUse =
      collections[tokenDatas[nft_id].idCollection];

    require(
      ERC20(USDTAddress).transferFrom(
        msg.sender,
        recipient,
        amountOfDays.mul(collectionToUse.desactiveCost).div(10**30)
      ),
      "The usdt transfer failed"
    );

    userInfo[msg.sender].inactivoPays[nft_id].push(pagoNFTinactivo({
      amountOfDays:amountOfDays,
      amountOfUSDT:amountOfDays.mul(collectionToUse.desactiveCost).div(10**30),
      date:block.timestamp
    }));
    emit desactivePaid(nft_id, msg.sender,amountOfDays);
  }


  function buyToken(
    uint256 _amount,
    uint256 _collectionID
  ) public nonReentrant isPublicCollection(_collectionID) {
    Collection memory collectionToUse = collections[_collectionID];
    require(
      collectionToUse.remainAmountToSell >= _amount,
      "There is not enough amount in this collection"
    );
    require(
      _collectionID > 0 && _collectionID <= idCollection,
      "This id is not in collection"
    );
    require(
      ERC20(USDTAddress).transferFrom(
        msg.sender,
        recipient,
        _amount.mul(collectionToUse.price).div(10**30)
      ),
      "The usdt transfer failed"
    );

    ///Repetir estre proceso para varios NFT hasta llegar a amount 
    for (uint i=0;i<_amount.div(10**18);i++) {
    _mint(msg.sender, nftId, 1**18, "0x");
    tokenDatas[nftId] = tokenData({
      creator: msg.sender,
      idCollection: _collectionID,
      born: block.timestamp,
      active:true
    });
    userInfo[msg.sender].idsForUser.push(nftId);
    nftsOfCollection[_collectionID].push(nftId);
    nftId++;
    }
    collections[_collectionID].remainAmountToSell = collections[_collectionID]
      .remainAmountToSell
      .sub(_amount);
    if (
      collections[_collectionID].amount.sub(
        collections[_collectionID].remainAmountToSell
      ) >= collectionToUse.amountToStartMaduration
    ) {
      collections[_collectionID].madurationStartedAt = block.timestamp;
    }

    emit Purchased(_amount, block.timestamp);
  }
  /// function to desactive an nft 
  function desactive_ActivateNFT(address _user,uint256 _nftId) public  {
    require(tokenDatas[_nftId].creator==_user,"This Nft does match with user");
    require(_user==msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),"Cannot desactivate nft");
   tokenDatas[_nftId].active? tokenDatas[_nftId].active=false: tokenDatas[_nftId].active= true;
  }
  /// add a buy from other payment 
   function addExternalBuy(
    address _userAddress,
    uint256 _amount,
    uint256 _collectionID
  ) public nonReentrant onlyOwner isPublicCollection(_collectionID) {
    Collection memory collectionToUse = collections[_collectionID];
    require(
      collectionToUse.remainAmountToSell >= _amount,
      "There is not enough amount in this collection"
    );
    require(
      _collectionID > 0 && _collectionID <= idCollection,
      "This id is not in collection"
    );
   ///Repetir estre proceso para varios NFT hasta llegar a amount 
    for (uint i=0;i<_amount.div(10**18);i++) {
    _mint(_userAddress, nftId, 1, "");
    tokenDatas[nftId] = tokenData({
      creator: _userAddress,
      idCollection: _collectionID,
      born: block.timestamp, 
      active:true
    });
    userInfo[_userAddress].idsForUser.push(nftId);
    nftsOfCollection[_collectionID].push(nftId);
    nftId++;
    }
    collections[_collectionID].remainAmountToSell = collections[_collectionID]
      .remainAmountToSell
      .sub(_amount);
    if (
      collections[_collectionID].amount.sub(
        collections[_collectionID].remainAmountToSell
      ) >= collectionToUse.amountToStartMaduration
    ) {
      collections[_collectionID].madurationStartedAt = block.timestamp;
    }

    emit Purchased(_amount, block.timestamp);
  }

  /// ONLY OWNER FUNCTIONS
  function addWhitelist(address[] memory _accounts) public onlyOwner {
    for (uint256 i = 0; i < _accounts.length; i++) {
      whitelisted[_accounts[i]] = true;
    }
    whitelistArr = _accounts;
  }

  function removeWhitelistedAddress() public onlyOwner {
    for (uint256 i = 0; i < whitelistArr.length; i++) {
      whitelisted[whitelistArr[i]] = true;
    }
    delete whitelistArr;
  }

  /**
   * @dev user burns his NFTs
   */
  function burnIt(uint256 tokenId, uint256 amount) public onlyOwner {
    burn(msg.sender, tokenId, amount);
    emit BurnNFT(msg.sender, amount, tokenId);
  }

  /// Change usdt address
  function changeUSDTaddress(address _newAddress) public onlyOwner {
    USDTAddress = _newAddress;
  }

  /**
   * @dev admin create collections without upload ipfs
   */
  function createCollection(
    uint256 _price,
    string memory _description,
    bool _privacy,
    uint256 _TH,
    uint256 _madurityTime,
    uint256 _energyCost,
   uint256 _desactiveCost,
    uint256 _tokenLife,
    uint256 _amount,
    uint256 _amountToStartMaduration,
    string memory _ipfsHash
  ) public onlyOwner {
    require(
      _amount > _amountToStartMaduration,
      "Maduration amount should be less than total amount"
    );
    collections[idCollection] = Collection({
      isPublic: _privacy,
      desactiveCost:_desactiveCost,
      madurationStartedAt: 0,
      remainAmountToSell: _amount,
      TH: _TH,
      price: _price,
      madurityTime: _madurityTime,
      tokenLife: _tokenLife,
      amount: _amount,
      amountToStartMaduration: _amountToStartMaduration,
      energyCost: _energyCost,
      description: _description,
      ipfsHash: _ipfsHash
    });
    idCollection++;
    emit CollectionCreated(
      _price,
      _madurityTime,
      _tokenLife,
      _amount,
      _energyCost,
      _description,
      _ipfsHash
    );
  }

  /**
   * @dev returns the ipfs uri to get token data
